mapper.select:
SELECT * FROM ( SELECT A.FNUM FNUM ,A.FNAME FNAME ,A.FPRICE FPRICE ,A.FMOOD FMOOD ,A.FFILE FFILE ,A.DELETEYN DELETEYN
,CEIL(ROW_NUMBER() OVER(ORDER BY A.FNUM)/ #{pageSize} ) pageSize ,COUNT(A.FNUM) OVER() AS totalCount FROM FUNITURE_TABLE A WHERE A.DELETEYN = 'Y' ORDER BY 1 DESC
) WHERE pageSize = #{curPage}

mapper.update:
UPDATE FKART_TABLE
SET KDELETEYN = 'N'
WHERE KNUM = #{fnum}

update.foreach:
 
		UPDATE 	FKART_TABLE 
		SET 	KDELETEYN = 'N'		
		WHERE KNUM = #{item.fnum}

mapper.update:
UPDATE FUNITURE_TABLE 
SET
    FNAME = #{fname},
    FPRICE = #{fprice},
    FMOOD = #{fmood}
WHERE FNUM = #{fnum}
